import React, { Component } from 'react';
import 'github-markdown-css';
import 'normalize.css';
import marked from 'marked';
import cx from 'classnames';
import Swal from 'sweetalert2';
import axios from 'axios';
import dateUtil from './utils/date';
import request from './utils/request';
import './App.scss';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      notebooks: [],
      currentBookIndex: 0,

      notes: [],

      currentNote: null
    }
  }

  componentDidMount() {
    //渲染 notebooks 列表
    // axios.get('http://localhost:3100/notebooks').then(res => {
    //   this.setState({ notebooks: res.data });

    //   let book = res.data[this.state.currentBookIndex];
    //   this.loadNotes(book.id);
    // })

    document.title = '实战Evernote';
    window.addEventListener('beforeunload', () => {
      var data = {
        currentBookIndex: this.state.currentBookIndex,
        currentNoteId: this.state.currentNote ? this.state.currentNote.id : null
      }
      localStorage.setItem('evernoteEditData', JSON.stringify(data));
    });
    console.log(data)

    var json = localStorage.getItem('evernoteEditData')
    if (json) {
      var data = JSON.parse(json);
      this.setState({ currentBookIndex: data.currentBookIndex })
    }
    console.log(data)
    this.loadNotebooks(data);
  }

  render() {
    let notebooks = this.state.notebooks;
    let notes = this.state.notes;
    let currentNote = this.state.currentNote;
    return (
      <div className="app">
        <div className="sidebar">
          <div className="header">
            <button className="button adder" onClick={() => this.handleAddNote()}>
              <i className="iconfont icon-add"></i>
                新建笔记
              </button>
          </div>

          <div className="body">
            <div className="notebooks">
              <div className="header has-icon">
                <i className="iconfont icon-books"></i>
                笔记本
              </div>
              <div className="body">
                <ul className="nodebooks-list">
                  {
                    notebooks.map((notebook, index) => (
                      <li key={notebook.id}
                        className={cx('notebook-item', { active: this.state.currentBookIndex === index })}
                        // className={'notebook-item '+(this.state.currentBookIndex === index?'active':'') }
                        onClick={() => this.handleBookSelect(index)}>
                        <div className="title has-icon">
                          <i className="iconfont icon-book"></i>
                          {notebook.name}
                        </div>
                        <button className="button trash"><i className="iconfont icon-trash"></i></button>
                      </li>
                    ))
                  }
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className="notes-panel">
          <div className="header">读书笔记</div>
          <div className="body">
            <ul className="notes-list">
              {
                notes.map((note) => (
                  <li key={note.id}>
                    <div className={cx('note-brief', { active: currentNote && currentNote.id === note.id })}>
                      <div className="box"
                        onClick={() => this.loadNote(note.id)}>
                        <div className="header">{note.title}</div>
                        <div className="body">{getBrief(note.body)}</div>
                      </div>
                      <div className="footer">
                        <div className="datetime">
                          {note.datetime.toISOString()}
                        </div>
                        <button className="trash button" onClick={() => this.requestDeleteNote(note.id)}>
                          <i className="iconfont icon-trash"></i>
                        </button>
                      </div>
                    </div>
                  </li>
                ))
              }
            </ul>
          </div>
        </div>
        {currentNote ?
          <div className="note-panel">
            <div className="header">
              <div className="category has-icon">
                <i className="iconfont icon-notebook"></i>
              读书笔记3
            </div>
              <div className="title">
                <input type="text" value={currentNote.title || ''}
                  onChange={e => this.updateNote('title', e.target.value)} />
              </div>
            </div>

            <div className="body">
              <div className="editor">
                <textarea className="textrea" value={currentNote.body}
                  onChange={(e) => this.updateNote('body', e.target.value)}></textarea>
                <div className="preview"></div>
              </div>
              <div className="preview markdown-body">
                {/* <div dangerouslySetInnerHTML={{ __html: marked(currentNote.body) }}></div> */}
              </div>
            </div>
          </div> : null
        }
      </div>
    );
  }
  handleEditNote(id) {
    axios.get('http://localhost:3100/notes' + id).then(
      res => { this.setState({ currentNote: res.data }) }
    )
  }
  updateNote(field, value) {
    let note = this.state.currentNote;
    note[field] = value;
    console.log(note)
    this.setState({ currentNote: note })
    // axios.post('http://localhost:3100/notes',currentNote)
  }
  //添加新笔记
  handleAddNote() {
    let book = this.state.notebooks[this.state.currentBookIndex];
    let note = {
      title: '新建笔记',
      body: '',
      datetime: new Date().toISOString(),
      bookId: book.id
    }
    axios.post('http://localhost:3100/notes', note).then(
      this.reloadNotes(book.id))
  }
  //渲染noteBooks列表
  handleBookSelect(index) {
    this.setState({ currentBookIndex: index });
    var book = this.state.notebooks[index];
    //从后台拉取数据
    this.loadNotes(book.id)
  }
  //获取笔记本列表
  loadNotebooks(editData) {
    editData = editData || {};
    request('/notebooks/').then(notebooks => {

      this.setState({ notebooks: notebooks }, () => {
        var bookIndex = editData.currentBookIndex || 0;
        this.loadNotes(bookIndex);
        // if(editData.currentNoteId){
        //   this.loadNote(editData.currentNoteId)
        // }
      })
    })
  }

  //渲染notes列表
  loadNotes(id) {
    axios.get('http://localhost:3100/notes?bookId=' + id).then(
      note => {
        // console.log(note);
        this.setState({ notes: note.data });
      })
  }
  reloadNotes() {
    let book = this.state.notebooks[this.state.currentBookIndex];
    this.loadNotes(book.id)
  }
  loadNote(id) {
    // request('/notes/' + id).then(
    //   note=>this.setState({ currentNote: note })
    // )
  }
  requestDeleteNote() { }
}
export default App;

function getBrief(body) {
  body = body || '';
  return body.length > 100 ? body.substr(0, 70) + '...' : body;
}
