import React, { Component } from 'react';
import 'marked';
import 'github-markdown-css';
import 'normalize.css';

import './App.scss';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      markdown:''
    }
  }

  render() {
    return (
      <div className="app">
          <div className="one">
            <div>
              <h2><i>icon</i> 新建笔记</h2>
            </div>
            <div>
              <i></i>笔记本
              <div className="1"><i className="icon"></i>默认笔记本</div>
              <div className="1"><i className="icon"></i>我的2020</div>
              <div className="1"><i className="icon"></i>读书笔记</div>
            </div>
          </div>

          <div className="two">
            <h1>读书笔记</h1>
            <hr/>
            <div className="content"></div>
          </div>

          <div className="three">
          <div>  <span><i className="icon"></i>读书笔记</span> </div>
          <hr/>
          <div className="markdown">
            <div className="tittle">深入理解ES6</div>
            <div  className="eidor">
              <textarea className="textrea" value="markdownEditorValue" onChange={(e)=>this.onChange(e)}></textarea>
              <div className="preview"></div>
            </div>
          </div>
          </div>
      </div>
    );
  }
  onChange(e){
    let value = e.target.value;
  }
}

export default App;
