import React, { Component } from 'react';
// import marked from 'marked';
import cx from 'classnames';
// import Swal from 'sweetalert2';
import axios from 'axios';
import 'github-markdown-css';
import 'normalize.css';

import './App.scss';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      notebooks: [],

      currentBookIndex: 0,
      notes: [     {
        "title": "新建笔记",
        "body": "",
        "datetime": "Mon Mar 11 2019 16:43:34 GMT+0800 (China Standard Time)",
        "bookId": 1,
        "id": 4
      },
      {
        "title": "新建笔记",
        "body": "",
        "datetime": "Mon Mar 11 2019 16:45:02 GMT+0800 (China Standard Time)",
        "bookId": 1,
        "id": 5
      }],

      currentNote: null
    }
  }

  componentDidMount() {
    //渲染 notebooks 列表
    axios.get('http://localhost:3100/notebooks').then(res => {
      this.setState({ notebooks: res.data });

      let book = res.data[this.state.currentBookIndex];
      this.loadNotes(book.id);
    })

    // document.title = 'Evernote';
    // window.addEventListener('beforeunload', () => {
    //   var data = {
    //     currentBookIndex: this.state.currentBookIndex,
    //     currentNoteId: this.state.currentNote ? this.state.currentNote.id : null
    //   };
    //   localStorage.setItem('evernoteEditData', JSON.stringify(data))
    // });
    // let json = localStorage.getItem('evernoteEditData');

    // if (json) {
    //   var data = JSON.parse(json);
    //   this.setState({ currentBookIndex: data.currentBookIndex })
    // }
    // this.loadNotebooks(data);
  }

  render() {
    let notebooks = this.state.notebooks;
    // let notes = this.state.notes;
    let currentNote = this.state.currentNote;
    return (
      <div className="app">
        <div className="sidebar">
          <div className="header">
            <button className="button adder">
              <i className="iconfont icon-add"></i>
                新建笔记
              </button>
          </div>

          <div className="body">
            <div className="notebooks">
              <div className="header has-icon">
                <i className="iconfont icon-books"></i>
                笔记本
              </div>
              <div className="body">
                <ul className="nodebooks-list">
                  {
                    notebooks.map((notebook, index) => (
                      <li key={notebook.id}
                        className={cx('notebook-item', { active: this.state.currentBookIndex === index })}
                        // className={'notebook-item '+(this.state.currentBookIndex === index?'active':'') }
                        onClick={() => this.handleBookSelect(index)}>
                        <div className="title has-icon">
                          <i className="iconfont icon-book"></i>
                          {notebook.name}
                        </div>
                        <button className="button trash"><i className="iconfont icon-trash"></i></button>
                      </li>
                    ))
                  }
                </ul>
              </div>
            </div>
          </div>
        </div>

        <div className="notes-panel">
          <div className="header">读书笔记</div>
          <div className="body">
            <ul className="notes-list">
              {
                this.state.notes.map((note) => {
                  <li key={note.id}>
                    <div className='note-brief'>
                      <div className="box">
                        <div className="header">{note.title}</div>
                        <div className="body">{getBrief(note.body)}</div>
                      </div>
                      <div className="footer">
                        <div className="datetime">
                          {note.datetime}
                        </div>
                        <button className="trash button" onClick={() => this.requestDeleteNote(note.id)}>
                          <i className="iconfont icon-trash"></i>
                        </button>
                      </div>
                    </div>
                  </li>
                })
              }
            </ul>
          </div>
        </div>

        <div className="note-panel">
          <div className="header">
            <div className="category has-icon">
              <i className="iconfont icon-notebook"></i>
              读书笔记3
            </div>
            <div className="title">
              <ul>
                {
                  <li></li>
                }
              </ul>
            </div>
          </div>

          <div className="body">
            <div className="editor">
              <textarea className="textrea" value="markdownEditorValue" onChange={(e) => this.onChange(e)}></textarea>
              <div className="preview"></div>
            </div>
            <div className="preview markdown-body">
              {/* <div dangerouslySetInnerHTML={{  }}></div> */}
            </div>
          </div>
        </div>

      </div>
    );
  }
  onChange(e) {
    // let value = e.target.value;
  }
  //渲染notes列表
  // loadNotebooks(editData) {

  // }

  //渲染noteBooks列表
  handleBookSelect(index) {
    this.setState({ currentBookIndex: index });
    var book = this.state.notebooks[index];
    //从后台拉取数据
    // this.loadNotes(book.id)
  }

  loadNotes(id) {
    axios.get('http://localhost:3100/notes?bookId=' + id).then(
      note => {
        console.log(note);

        // this.setState({ notes: note.data });
        console.log(this.state.notes);
      })
  }

  requestDeleteNote() { }
}
export default App;

function getBrief(body) {
  body = body || '';
  return body.length > 100 ? body.substr(0, 70) + '...' : body;
}
